import React from "react";
import arrow from "../images/arrow.png";

class LocSheetMenu extends React.Component {
  state = {
    classStyle: "loc-sheet-menu-closed",
  };

  render() {
    const toogleMenu = (e) => {
        if (e.target.className == "arrow") {
          e.target.classList = "close-arrow";
          this.setState({
            classStyle: "sub-menu-composant",
          });
        } else {
          e.target.classList = "arrow";
          this.setState({
            classStyle: "sub-menu-composant-closed",
          });
        }
      };

    return (
      <div className="menu-composant-wrap">
        <div className="menu-composant">
          <p>
            {this.props.descript}
            {this.props.equipement}
          </p>
          <img
            className="arrow"
            src={arrow}
            onClick={(e) => toogleMenu(e)}
          ></img>
        </div>

        <div
          className={ this.state.classStyle}

        >
          <p>{this.props.Descriptions}</p>
        </div>
      </div>
    );
  }
}

export default LocSheetMenu;
