import React from "react";
import "../style/_menu-composant.scss";
import arrow from "../images/arrow.png";
import { useState } from "react";

class MenuComposant extends React.Component {
  render() {
    const toogleMenu = () => {

      let isOpen = true;
      let arrowStyle;
      console.log(arrowStyle)
      console.log(isOpen);
      if (isOpen == true) {
        arrowStyle = "close-arrow";
        isOpen = false
      } else {
        arrowStyle ="arrow";
      }
      console.log(arrowStyle);
      console.log(isOpen);
    };

    return (
      <div className="menu-composant-wrap">
        <div className="menu-composant">
          <p>
            {this.props.descript}
            {this.props.equipement}
          </p>
          <img className="" src={arrow} onClick={() => toogleMenu()}></img>
        </div>
        <div className="sub-menu-composant">
          <p>{this.props.Descriptions}</p>
        </div>
      </div>
    );
  }
}

export default MenuComposant;
